import React from 'react';
import { useState } from 'react';

export function Chatroom() {
  return (
    <div style={{
      backgroundColor: '#272727'
    }}>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <p>{'Menu'}</p>
        <p>{'Chatroom: LPRB'}</p>
      </div>
      <div>
        <SideBar />
        <div>
          <ChatHeader chatname='IN4MATX 117 DISCUSSION' topic='Software Design'/>
          <ChatBox />
        </div>
      </div>
    </div>
  );
}

function SideBar() {
  return <div></div>;
}

interface ChatHeaderProps {
  chatname: string,
  topic: string
}

function ChatHeader(props : ChatHeaderProps) {
  return (
    <div
      style={{
        backgroundColor: '#251D16',
        color: '#FFC690',
        borderRadius: 20,
        justifyContent: 'space-between',
        alignItems: 'center',
        display: 'flex',
        paddingRight: 20,
        paddingLeft: 20,
      }}
    >
      <p>{props.chatname}</p>
      <p>{'Topic: '+ props.topic}</p>
    </div>
  );
}

interface MessageProps {
  message : string
}

function ChatBox() {
  const Message = (props : MessageProps) => {
    return (
      <div style={{display: 'flex', alignItems: 'center'}}>
        <div style={{
            width: 49,
            height: 49,
            backgroundColor: '#B2B2B2',
            borderRadius: 50,
            marginRight: 15
        }}/>
        <div>
          <p style={{ fontSize: 18, color: '#5C5C5C', fontWeight: 600}}>Tariq Brown</p>
          <p style={{ fontSize: 18 }}>{props.message}</p>
        </div>

      </div>
    )
  }

  const [messages, setMessages] = useState<JSX.Element[]>([]);

  const MessageInput = () => {
    const [input, setInput] = useState('');

    const handleSubmit = (e: React.FormEvent) => {
      e.preventDefault();
      setMessages([...messages, <Message message={input}/>]);
      setInput('');
    };
  
    return (
      <div>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
          />
          <button type="submit">Send</button>
        </form>
      </div>
    );
  }

  return (
    <div>
      <div style={{
        backgroundColor: '#FBFBFB',
        height: 587,
        width: 993,
        borderRadius: 20,
        paddingRight: 20,
        paddingLeft: 20,
      }}>
        {messages}
      </div>
      <MessageInput />
    </div>
  )
}
